var fse = require('fs-extra');
var ArgumentError = require('./error').ArgumentError;

module.exports = {
    fs: fse,
    writeJsonToFile: function writeJsonToFile(objectToWrite, filePath, callback) {
        if (typeof objectToWrite !== 'object') return callback(new ArgumentError('must provide an object for writing'));
        if (typeof filePath !== 'string') return callback(new ArgumentError('must provide a valid file path'));

        this.fs.outputJsonSync(filePath, objectToWrite);
        return callback();
    }
};